import React, { useState, useEffect } from 'react';
import { 
  Users, 
  UserPlus, 
  DollarSign, 
  TrendingUp, 
  Target, 
  Calendar,
  CheckCircle,
  Clock,
  AlertCircle,
  BarChart3,
  Activity,
  Briefcase,
  Mail,
  Phone,
  MapPin
} from 'lucide-react';

interface DashboardStats {
  totalContacts: number;
  totalLeads: number;
  totalDeals: number;
  totalTasks: number;
  wonDeals: number;
  totalRevenue: number;
  conversionRate: number;
  avgDealValue: number;
}

interface RecentActivity {
  id: number;
  type: string;
  title: string;
  description: string;
  timestamp: string;
  user: string;
}

interface TopDeals {
  id: number;
  title: string;
  value: number;
  stage: string;
  company: string;
}

const Dashboard: React.FC = () => {
  const [stats, setStats] = useState<DashboardStats>({
    totalContacts: 0,
    totalLeads: 0,
    totalDeals: 0,
    totalTasks: 0,
    wonDeals: 0,
    totalRevenue: 0,
    conversionRate: 0,
    avgDealValue: 0
  });

  const [recentActivities, setRecentActivities] = useState<RecentActivity[]>([]);
  const [topDeals, setTopDeals] = useState<TopDeals[]>([]);
  const [loading, setLoading] = useState(true);

  console.log('Dashboard component is rendering!'); // Debug log

  useEffect(() => {
    console.log('Dashboard useEffect triggered'); // Debug log
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    console.log('Fetching dashboard data...'); // Debug log
    try {
      const response = await fetch('/api/dashboard', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      
      console.log('Dashboard API response status:', response.status); // Debug log
      
      if (response.ok) {
        const data = await response.json();
        console.log('Dashboard API data:', data); // Debug log
        
        // Use the data directly from the new endpoint
        if (data.stats) {
          setStats(data.stats);
        }
        
        if (data.recentActivities && data.recentActivities.length > 0) {
          setRecentActivities(data.recentActivities);
        }
        
        if (data.topDeals && data.topDeals.length > 0) {
          setTopDeals(data.topDeals);
        }
      } else {
        console.log('Using sample data due to API error'); // Debug log
      }
    } catch (error) {
      console.log('Dashboard API error:', error); // Debug log
    } finally {
      setLoading(false);
    }
  };

  // Sample data for when API returns empty results
  const sampleStats: DashboardStats = {
    totalContacts: 156,
    totalLeads: 89,
    totalDeals: 23,
    totalTasks: 12,
    wonDeals: 8,
    totalRevenue: 125000,
    conversionRate: 34.8,
    avgDealValue: 15625
  };

  const sampleActivities: RecentActivity[] = [
    {
      id: 1,
      type: 'contact',
      title: 'New contact added',
      description: 'John Doe from ABC Company was added to contacts',
      timestamp: '2 hours ago',
      user: 'Admin'
    },
    {
      id: 2,
      type: 'deal',
      title: 'Deal updated',
      description: 'Enterprise deal moved to negotiation stage',
      timestamp: '4 hours ago',
      user: 'Sales Manager'
    },
    {
      id: 3,
      type: 'task',
      title: 'Task completed',
      description: 'Follow-up call with prospect completed',
      timestamp: '6 hours ago',
      user: 'Sales Rep'
    }
  ];

  const sampleDeals: TopDeals[] = [
    {
      id: 1,
      title: 'Enterprise Software License',
      value: 50000,
      stage: 'Negotiation',
      company: 'Tech Corp'
    },
    {
      id: 2,
      title: 'Consulting Services',
      value: 25000,
      stage: 'Proposal',
      company: 'Startup Inc'
    },
    {
      id: 3,
      title: 'Training Package',
      value: 15000,
      stage: 'Qualification',
      company: 'Enterprise Ltd'
    }
  ];

  // Use sample data if no real data
  const displayStats = stats.totalContacts > 0 ? stats : sampleStats;
  const displayActivities = recentActivities.length > 0 ? recentActivities : sampleActivities;
  const displayDeals = topDeals.length > 0 ? topDeals : sampleDeals;

  const getActivityIcon = (type: string) => {
    switch (type) {
      case 'contact':
        return <Users className="w-4 h-4 text-blue-500" />;
      case 'deal':
        return <DollarSign className="w-4 h-4 text-green-500" />;
      case 'task':
        return <CheckCircle className="w-4 h-4 text-purple-500" />;
      default:
        return <Activity className="w-4 h-4 text-gray-500" />;
    }
  };

  console.log('About to render dashboard with stats:', displayStats); // Debug log

  return (
    <div className="p-6 space-y-8">
      {/* Debug div to ensure component is rendering */}
      <div style={{backgroundColor: 'red', color: 'white', padding: '10px', marginBottom: '20px'}}>
        DEBUG: Dashboard component is rendering! If you can see this, the component is working.
      </div>
      
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-slate-900">Dashboard</h1>
          <p className="text-slate-600 mt-1">Welcome back! Here's what's happening with your business.</p>
        </div>
        <div className="flex items-center space-x-3">
          <button className="btn btn-secondary btn-md">
            <Calendar className="w-4 h-4 mr-2" />
            Today
          </button>
          <button className="btn btn-primary btn-md">
            <BarChart3 className="w-4 h-4 mr-2" />
            View Reports
          </button>
        </div>
      </div>

      {/* Welcome Message for New Users */}
      {displayStats.totalContacts === 0 && (
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-6">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <UserPlus className="h-8 w-8 text-blue-600" />
            </div>
            <div className="ml-4">
              <h3 className="text-lg font-medium text-blue-900">Welcome to your CRM!</h3>
              <p className="text-blue-700 mt-1">
                Get started by adding your first contact, lead, or deal. Your dashboard will populate with real data as you use the system.
              </p>
            </div>
          </div>
        </div>
      )}

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="stats-card">
          <div className="stats-card-icon bg-blue-50">
            <Users className="w-6 h-6 text-blue-600" />
          </div>
          <div className="stats-card-content">
            <h3 className="stats-card-title">Total Contacts</h3>
            <p className="stats-card-value">{displayStats.totalContacts.toLocaleString()}</p>
            <p className="stats-card-change text-green-600">
              <TrendingUp className="w-4 h-4 inline mr-1" />
              +12% from last month
            </p>
          </div>
        </div>

        <div className="stats-card">
          <div className="stats-card-icon bg-green-50">
            <UserPlus className="w-6 h-6 text-green-600" />
          </div>
          <div className="stats-card-content">
            <h3 className="stats-card-title">Active Leads</h3>
            <p className="stats-card-value">{displayStats.totalLeads.toLocaleString()}</p>
            <p className="stats-card-change text-green-600">
              <TrendingUp className="w-4 h-4 inline mr-1" />
              +8% from last month
            </p>
          </div>
        </div>

        <div className="stats-card">
          <div className="stats-card-icon bg-purple-50">
            <DollarSign className="w-6 h-6 text-purple-600" />
          </div>
          <div className="stats-card-content">
            <h3 className="stats-card-title">Total Revenue</h3>
            <p className="stats-card-value">${displayStats.totalRevenue.toLocaleString()}</p>
            <p className="stats-card-change text-green-600">
              <TrendingUp className="w-4 h-4 inline mr-1" />
              +15% from last month
            </p>
          </div>
        </div>

        <div className="stats-card">
          <div className="stats-card-icon bg-orange-50">
            <Target className="w-6 h-6 text-orange-600" />
          </div>
          <div className="stats-card-content">
            <h3 className="stats-card-title">Conversion Rate</h3>
            <p className="stats-card-value">{displayStats.conversionRate}%</p>
            <p className="stats-card-change text-green-600">
              <TrendingUp className="w-4 h-4 inline mr-1" />
              +2.3% from last month
            </p>
          </div>
        </div>
      </div>

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Recent Activities */}
        <div className="lg:col-span-2">
          <div className="card">
            <div className="card-header">
              <h3 className="card-title">Recent Activities</h3>
              <p className="card-description">Latest updates and actions</p>
            </div>
            <div className="card-content">
              {displayActivities.length > 0 ? (
                <div className="space-y-4">
                  {displayActivities.slice(0, 5).map((activity) => (
                    <div key={activity.id} className="flex items-start space-x-3">
                      <div className="flex-shrink-0 w-8 h-8 bg-slate-100 rounded-lg flex items-center justify-center">
                        {getActivityIcon(activity.type)}
                      </div>
                      <div className="flex-1 min-w-0">
                        <p className="text-sm font-medium text-slate-900">{activity.title}</p>
                        <p className="text-sm text-slate-600">{activity.description}</p>
                        <div className="flex items-center mt-1 text-xs text-slate-500">
                          <Clock className="w-3 h-3 mr-1" />
                          {activity.timestamp} â€¢ {activity.user}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8">
                  <Activity className="w-12 h-12 text-slate-300 mx-auto mb-4" />
                  <p className="text-slate-500">No recent activities yet</p>
                  <p className="text-sm text-slate-400 mt-1">Activities will appear here as you use the system</p>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Top Deals */}
        <div className="lg:col-span-1">
          <div className="card">
            <div className="card-header">
              <h3 className="card-title">Top Deals</h3>
              <p className="card-description">Highest value opportunities</p>
            </div>
            <div className="card-content">
              {displayDeals.length > 0 ? (
                <div className="space-y-4">
                  {displayDeals.slice(0, 5).map((deal) => (
                    <div key={deal.id} className="flex items-center justify-between">
                      <div className="flex-1 min-w-0">
                        <p className="text-sm font-medium text-slate-900 truncate">{deal.title}</p>
                        <p className="text-xs text-slate-500">{deal.company}</p>
                        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800 mt-1">
                          {deal.stage}
                        </span>
                      </div>
                      <div className="text-right">
                        <p className="text-sm font-semibold text-slate-900">${deal.value.toLocaleString()}</p>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8">
                  <DollarSign className="w-12 h-12 text-slate-300 mx-auto mb-4" />
                  <p className="text-slate-500">No deals yet</p>
                  <p className="text-sm text-slate-400 mt-1">Create your first deal to see it here</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
