import React, { useState, useEffect } from 'react';
import { 
  Users, 
  UserPlus, 
  DollarSign, 
  TrendingUp, 
  Target, 
  Calendar,
  CheckCircle,
  Clock,
  AlertCircle,
  BarChart3,
  Activity,
  Briefcase,
  Mail,
  Phone,
  MapPin
} from 'lucide-react';

interface DashboardStats {
  totalContacts: number;
  totalLeads: number;
  totalDeals: number;
  totalTasks: number;
  wonDeals: number;
  totalRevenue: number;
  conversionRate: number;
  avgDealValue: number;
}

interface RecentActivity {
  id: number;
  type: string;
  title: string;
  description: string;
  timestamp: string;
  user: string;
}

interface TopDeals {
  id: number;
  title: string;
  value: number;
  stage: string;
  contact: string;
  probability: number;
}

const Dashboard: React.FC = () => {
  const [stats, setStats] = useState<DashboardStats>({
    totalContacts: 0,
    totalLeads: 0,
    totalDeals: 0,
    totalTasks: 0,
    wonDeals: 0,
    totalRevenue: 0,
    conversionRate: 0,
    avgDealValue: 0
  });
  const [recentActivities, setRecentActivities] = useState<RecentActivity[]>([]);
  const [topDeals, setTopDeals] = useState<TopDeals[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const response = await fetch('/api/dashboard', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      const data = await response.json();
      
      setStats(data.stats);
      setRecentActivities(data.recentActivities || []);
      setTopDeals(data.topDeals || []);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  const getStageColor = (stage: string) => {
    switch (stage) {
      case 'prospecting': return 'bg-blue-50 text-blue-700';
      case 'qualification': return 'bg-purple-50 text-purple-700';
      case 'proposal': return 'bg-orange-50 text-orange-700';
      case 'negotiation': return 'bg-yellow-50 text-yellow-700';
      case 'closed_won': return 'bg-green-50 text-green-700';
      case 'closed_lost': return 'bg-red-50 text-red-700';
      default: return 'bg-gray-50 text-gray-700';
    }
  };

  const getActivityIcon = (type: string) => {
    switch (type) {
      case 'contact': return <Users className="w-4 h-4" />;
      case 'lead': return <UserPlus className="w-4 h-4" />;
      case 'deal': return <DollarSign className="w-4 h-4" />;
      case 'task': return <CheckCircle className="w-4 h-4" />;
      default: return <Activity className="w-4 h-4" />;
    }
  };

  if (loading) {
    return (
      <div className="p-6 space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {[...Array(4)].map((_, i) => (
            <div key={i} className="card">
              <div className="p-6">
                <div className="loading-skeleton h-8 w-24 mb-2"></div>
                <div className="loading-skeleton h-12 w-16"></div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-8">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-slate-900">Dashboard</h1>
          <p className="text-slate-600 mt-1">Welcome back! Here's what's happening with your business.</p>
        </div>
        <div className="flex items-center space-x-3">
          <button className="btn btn-secondary btn-md">
            <Calendar className="w-4 h-4 mr-2" />
            Today
          </button>
          <button className="btn btn-primary btn-md">
            <BarChart3 className="w-4 h-4 mr-2" />
            View Reports
          </button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="stats-card">
          <div className="stats-card-header">
            <div className="flex items-center justify-between">
              <div>
                <p className="stats-card-label">Total Contacts</p>
                <p className="stats-card-value">{stats.totalContacts.toLocaleString()}</p>
              </div>
              <div className="stats-card-icon bg-blue-50">
                <Users className="w-6 h-6 text-blue-600" />
              </div>
            </div>
            <div className="mt-4">
              <div className="flex items-center text-sm text-slate-600">
                <TrendingUp className="w-4 h-4 text-green-500 mr-1" />
                <span>+12% from last month</span>
              </div>
            </div>
          </div>
        </div>

        <div className="stats-card">
          <div className="stats-card-header">
            <div className="flex items-center justify-between">
              <div>
                <p className="stats-card-label">Active Leads</p>
                <p className="stats-card-value">{stats.totalLeads.toLocaleString()}</p>
              </div>
              <div className="stats-card-icon bg-purple-50">
                <UserPlus className="w-6 h-6 text-purple-600" />
              </div>
            </div>
            <div className="mt-4">
              <div className="flex items-center text-sm text-slate-600">
                <Target className="w-4 h-4 text-blue-500 mr-1" />
                <span>{stats.conversionRate}% conversion rate</span>
              </div>
            </div>
          </div>
        </div>

        <div className="stats-card">
          <div className="stats-card-header">
            <div className="flex items-center justify-between">
              <div>
                <p className="stats-card-label">Total Revenue</p>
                <p className="stats-card-value">{formatCurrency(stats.totalRevenue)}</p>
              </div>
              <div className="stats-card-icon bg-emerald-50">
                <DollarSign className="w-6 h-6 text-emerald-600" />
              </div>
            </div>
            <div className="mt-4">
              <div className="flex items-center text-sm text-slate-600">
                <TrendingUp className="w-4 h-4 text-green-500 mr-1" />
                <span>+8% from last month</span>
              </div>
            </div>
          </div>
        </div>

        <div className="stats-card">
          <div className="stats-card-header">
            <div className="flex items-center justify-between">
              <div>
                <p className="stats-card-label">Open Tasks</p>
                <p className="stats-card-value">{stats.totalTasks.toLocaleString()}</p>
              </div>
              <div className="stats-card-icon bg-amber-50">
                <CheckCircle className="w-6 h-6 text-amber-600" />
              </div>
            </div>
            <div className="mt-4">
              <div className="flex items-center text-sm text-slate-600">
                <Clock className="w-4 h-4 text-orange-500 mr-1" />
                <span>5 overdue tasks</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Charts and Analytics */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Sales Pipeline */}
        <div className="lg:col-span-2 card">
          <div className="card-header">
            <h3 className="card-title">Sales Pipeline</h3>
            <p className="card-description">Deal stages and their values</p>
          </div>
          <div className="card-content">
            <div className="space-y-4">
              {['Prospecting', 'Qualification', 'Proposal', 'Negotiation', 'Closed Won'].map((stage, index) => (
                <div key={stage} className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className="w-3 h-3 rounded-full bg-blue-500"></div>
                    <span className="text-sm font-medium text-slate-700">{stage}</span>
                  </div>
                  <div className="flex items-center space-x-4">
                    <span className="text-sm text-slate-600">${(Math.random() * 50000 + 10000).toLocaleString()}</span>
                    <div className="w-24 bg-slate-200 rounded-full h-2">
                      <div 
                        className="bg-gradient-to-r from-blue-500 to-indigo-500 h-2 rounded-full transition-all duration-300"
                        style={{ width: `${Math.random() * 100}%` }}
                      ></div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="card">
          <div className="card-header">
            <h3 className="card-title">Quick Actions</h3>
            <p className="card-description">Common tasks and shortcuts</p>
          </div>
          <div className="card-content">
            <div className="space-y-3">
              <button className="w-full btn btn-primary btn-md">
                <UserPlus className="w-4 h-4 mr-2" />
                Add New Lead
              </button>
              <button className="w-full btn btn-secondary btn-md">
                <DollarSign className="w-4 h-4 mr-2" />
                Create Deal
              </button>
              <button className="w-full btn btn-secondary btn-md">
                <CheckCircle className="w-4 h-4 mr-2" />
                Add Task
              </button>
              <button className="w-full btn btn-secondary btn-md">
                <Users className="w-4 h-4 mr-2" />
                Import Contacts
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Recent Activities and Top Deals */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Activities */}
        <div className="card">
          <div className="card-header">
            <h3 className="card-title">Recent Activities</h3>
            <p className="card-description">Latest updates and actions</p>
          </div>
          <div className="card-content">
            <div className="space-y-4">
              {recentActivities.slice(0, 5).map((activity) => (
                <div key={activity.id} className="flex items-start space-x-3">
                  <div className="flex-shrink-0 w-8 h-8 bg-slate-100 rounded-lg flex items-center justify-center">
                    {getActivityIcon(activity.type)}
                  </div>
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-slate-900">{activity.title}</p>
                    <p className="text-sm text-slate-600">{activity.description}</p>
                    <p className="text-xs text-slate-500 mt-1">
                      {new Date(activity.timestamp).toLocaleDateString()} • {activity.user}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Top Deals */}
        <div className="card">
          <div className="card-header">
            <h3 className="card-title">Top Deals</h3>
            <p className="card-description">Highest value opportunities</p>
          </div>
          <div className="card-content">
            <div className="space-y-4">
              {topDeals.slice(0, 5).map((deal) => (
                <div key={deal.id} className="flex items-center justify-between">
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-slate-900 truncate">{deal.title}</p>
                    <p className="text-sm text-slate-600">{deal.contact}</p>
                  </div>
                  <div className="flex items-center space-x-3">
                    <span className="text-sm font-semibold text-slate-900">
                      {formatCurrency(deal.value)}
                    </span>
                    <span className={`px-2 py-1 text-xs font-medium rounded-full ${getStageColor(deal.stage)}`}>
                      {deal.stage.replace('_', ' ')}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Performance Metrics */}
      <div className="card">
        <div className="card-header">
          <h3 className="card-title">Performance Metrics</h3>
          <p className="card-description">Key performance indicators</p>
        </div>
        <div className="card-content">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            <div className="text-center">
              <div className="text-2xl font-bold text-slate-900">{stats.wonDeals}</div>
              <div className="text-sm text-slate-600">Won Deals</div>
              <div className="mt-2">
                <div className="w-full bg-slate-200 rounded-full h-2">
                  <div 
                    className="bg-gradient-to-r from-emerald-500 to-teal-500 h-2 rounded-full"
                    style={{ width: `${(stats.wonDeals / stats.totalDeals) * 100}%` }}
                  ></div>
                </div>
              </div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-slate-900">{formatCurrency(stats.avgDealValue)}</div>
              <div className="text-sm text-slate-600">Avg Deal Value</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-slate-900">{stats.conversionRate}%</div>
              <div className="text-sm text-slate-600">Conversion Rate</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-slate-900">24</div>
              <div className="text-sm text-slate-600">Days to Close</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
