import React from 'react';

const user = {
  name: 'Admin User',
  email: 'admin@crm.com',
  role: 'Admin',
  avatar: 'https://ui-avatars.com/api/?name=Admin+User',
  joined: '2024-01-01',
};

const Profile: React.FC = () => {
  return (
    <div className="p-6 max-w-xl mx-auto">
      <div className="card p-6 flex flex-col items-center">
        <img
          src={user.avatar}
          alt="Avatar"
          className="w-24 h-24 rounded-full mb-4 border-4 border-blue-200"
        />
        <h2 className="text-2xl font-bold mb-1">{user.name}</h2>
        <p className="text-slate-500 mb-2">{user.role}</p>
        <p className="mb-2">{user.email}</p>
        <p className="text-xs text-slate-400 mb-4">Joined: {user.joined}</p>
        <button className="btn btn-primary w-full">Edit Profile</button>
      </div>
    </div>
  );
};

export default Profile;
