import React, { useState, useRef } from 'react';
import { Link, useLocation } from 'react-router-dom';

interface NavLink {
  name: string;
  path: string;
}

const Header: React.FC<{ navLinks: NavLink[] }> = ({ navLinks }) => {
  const location = useLocation();
  const [search, setSearch] = useState('');
  const [results, setResults] = useState<any>(null);
  const [showDropdown, setShowDropdown] = useState(false);
  const searchRef = useRef<HTMLInputElement>(null);
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);

  const handleSearch = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setSearch(value);
    if (timeoutRef.current) clearTimeout(timeoutRef.current);
    if (value.trim() === '') {
      setResults(null);
      setShowDropdown(false);
      return;
    }
    timeoutRef.current = setTimeout(() => {
      fetch(`/api/search?q=${encodeURIComponent(value)}`)
        .then(res => res.json())
        .then(data => {
          setResults(data);
          setShowDropdown(true);
        });
    }, 300);
  };

  const handleResultClick = (type: string, id: number) => {
    setShowDropdown(false);
    setSearch('');
    setResults(null);
    if (type === 'contacts') window.location.href = `/contacts/${id}`;
    if (type === 'leads') window.location.href = `/leads/${id}`;
    if (type === 'deals') window.location.href = `/deals/${id}`;
    if (type === 'tasks') window.location.href = `/tasks/${id}`;
  };

  return (
    <header className="fixed top-0 left-0 right-0 z-20 bg-white shadow flex items-center h-16 px-8">
      <div className="font-bold text-xl text-blue-600 mr-8">Open Source CRM</div>
      <nav className="flex gap-6">
        {navLinks.map((link) => (
          <Link
            key={link.path}
            to={link.path}
            className={`font-medium hover:text-blue-600 transition-colors ${location.pathname === link.path ? 'text-blue-600' : 'text-slate-700'}`}
          >
            {link.name}
          </Link>
        ))}
      </nav>
      <div className="relative w-full max-w-xs mx-4">
        <input
          ref={searchRef}
          type="text"
          className="w-full px-3 py-2 rounded border border-gray-300 focus:outline-none focus:ring focus:border-blue-400 dark:bg-gray-800 dark:text-white"
          placeholder="Search..."
          value={search}
          onChange={handleSearch}
          onFocus={() => search && setShowDropdown(true)}
          onBlur={() => setTimeout(() => setShowDropdown(false), 200)}
        />
        {showDropdown && results && (
          <div className="absolute z-50 left-0 right-0 mt-1 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded shadow-lg max-h-80 overflow-y-auto">
            {['contacts', 'leads', 'deals', 'tasks'].map(type =>
              results[type] && results[type].length > 0 ? (
                <div key={type}>
                  <div className="px-3 py-1 text-xs font-semibold text-gray-500 uppercase dark:text-gray-400">{type}</div>
                  {results[type].map((item: any) => (
                    <div
                      key={item.id}
                      className="px-3 py-2 hover:bg-blue-50 dark:hover:bg-gray-700 cursor-pointer"
                      onClick={() => handleResultClick(type, item.id)}
                    >
                      {type === 'contacts' && `${item.first_name} ${item.last_name} (${item.email})`}
                      {type === 'leads' && `${item.name} (${item.status})`}
                      {type === 'deals' && `${item.title} (${item.stage})`}
                      {type === 'tasks' && `${item.title} (${item.status})`}
                    </div>
                  ))}
                </div>
              ) : null
            )}
            {Object.values(results).every((arr: any) => arr.length === 0) && (
              <div className="px-3 py-2 text-gray-500 dark:text-gray-400">No results found.</div>
            )}
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;
